import React, { useState, useRef, useEffect, FC } from "react";
import { X, Heart, Users, Compass, Image } from "lucide-react";

interface Team {
  id: number;
  name: string;
  slogan: string;
  members: number;
  interests: string[];
  image: string;
  adjectives: string[];
}

interface TeamCardProps {
  team: Team;
  onSwipe: (direction: "left" | "right", teamId: number) => void;
  zIndex: number;
  isActive: boolean;
}

const TeamCard: FC<TeamCardProps> = ({ team, onSwipe, zIndex, isActive }) => {
  const [startX, setStartX] = useState<number>(0);
  const [offsetX, setOffsetX] = useState<number>(0);
  const [isDragging, setIsDragging] = useState<boolean>(false);
  const [exitDirection, setExitDirection] = useState<"left" | "right" | null>(null);
  const [cursorType, setCursorType] = useState<"grab" | "grabbing">("grab");

  const handleSwipeEnd = () => {
    setIsDragging(false);
    setCursorType("grab");

    if (offsetX > 100) {
      setExitDirection("right");
      setTimeout(() => onSwipe("right", team.id), 300);
    } else if (offsetX < -100) {
      setExitDirection("left");
      setTimeout(() => onSwipe("left", team.id), 300);
    } else {
      setOffsetX(0);
    }
  };

  return (
    <div
      className="absolute w-full max-w-sm h-[450px] bg-white rounded-xl shadow-lg flex flex-col transition-transform duration-300 ease-in-out"
      style={{
        top: "50%",
        left: "50%",
        transform: `translate(-50%, -50%) translateX(${offsetX}px) rotate(${offsetX / 10}deg) ${
          exitDirection === "right"
            ? "translateX(150%) rotate(30deg)"
            : exitDirection === "left"
            ? "translateX(-150%) rotate(-30deg)"
            : ""
        }`,
        zIndex,
        transition: isDragging ? "none" : "transform 0.5s ease-out, opacity 0.5s ease-out",
        cursor: cursorType,
      }}
      onMouseDown={(e) => {
        if (!isActive) return;
        setStartX(e.clientX);
        setIsDragging(true);
        setCursorType("grabbing");
      }}
      onMouseMove={(e) => {
        if (isDragging) setOffsetX(e.clientX - startX);
      }}
      onMouseUp={handleSwipeEnd}
      onMouseLeave={handleSwipeEnd}
      onTouchStart={(e) => {
        if (!isActive) return;
        setStartX(e.touches[0].clientX);
        setIsDragging(true);
        setCursorType("grabbing");
      }}
      onTouchMove={(e) => {
        if (isDragging) setOffsetX(e.touches[0].clientX - startX);
      }}
      onTouchEnd={handleSwipeEnd}
      onMouseEnter={() => setCursorType("grab")}
    >
      {/* Image Section */}
      <div className="h-40 w-full bg-cover bg-center rounded-t-xl" style={{ backgroundImage: `url(${team.image})` }} />

      {/* Info Section */}
      <div className="p-4 flex-1">
        <h2 className="text-xl font-bold text-gray-800">{team.name}</h2>
        <p className="text-gray-600 text-sm mb-2">{team.slogan}</p>

        <div className="flex items-center mb-3">
          <Users className="text-gray-500 mr-2" size={20} />
          <span className="text-gray-700">{team.members} members</span>
        </div>

        <h3 className="font-semibold text-gray-800 flex items-center">
          <Compass className="text-indigo-500 mr-2" size={20} />
          Interests
        </h3>
        <div className="flex flex-wrap gap-2 mt-2">
          {team.interests.map((interest, idx) => (
            <span key={idx} className="bg-indigo-100 text-indigo-800 px-2 py-1 rounded-full text-sm">
              {interest}
            </span>
          ))}
        </div>

        <h3 className="font-semibold text-gray-800 mt-3">Traits</h3>
        <div className="flex flex-wrap gap-2 mt-1">
          {team.adjectives.map((adj, idx) => (
            <span key={idx} className="bg-gray-200 text-gray-800 px-2 py-1 rounded-full text-sm">
              {adj}
            </span>
          ))}
        </div>
      </div>
      
      {/* Buttons */}
      <div className="flex justify-between px-4 pb-4">
        <button onClick={() => onSwipe("left", team.id)} className="bg-red-500 p-2 rounded-full text-white shadow-md">
          <X size={24} />
        </button>
        <button onClick={() => onSwipe("right", team.id)} className="bg-green-500 p-2 rounded-full text-white shadow-md">
          <Heart size={24} />
        </button>
      </div>
    </div>
  );
};

